    function calcOverall()
    {
        var count = 0;
        var step1 = 0;
        var step2 = 0;
        var takerate = 0;
        var tablet = 0;
        var tabletp = 0;
        var orderp = 0;
        var goal = 0;
        var goalp = 0;

        $('.crm_row').each( function(i) 
        {
            var crm_id = $(this).prop('id');

            if (!isNaN($('#crm1_' + crm_id).html()))
            {
                step1 += parseInt($('#crm1_' + crm_id).html());
                step2 += parseInt($('#crm2_' + crm_id).html());
                takerate += parseFloat($('#crm3_' + crm_id).html());
                tablet += parseInt($('#crm4_' + crm_id).html());
                tabletp += parseFloat($('#crm5_' + crm_id).html());
                orderp += parseFloat($('#crm6_' + crm_id).html());

                var goaltext = $('#crm8_' + crm_id).html();
                var pos = goaltext.indexOf(' / ');
                goal += parseInt(goaltext.substr(pos + 3));

                count ++;
            }
        });

        if (goal > 0)
            goalp = (step1 * 100 / goal).toFixed(2);

        if (count > 0)
        {
            $('#all1').html(step1);
            $('#all2').html(step2);
            $('#all3').html((takerate / count).toFixed(2));
            $('#all4').html(tablet);
            $('#all5').html((tabletp / count).toFixed(2));
            $('#all6').html((orderp / count).toFixed(2));
            $('#all7').html(goalp);
            $('#all8').html(step1 + ' / ' + goal);
        }
    }

	function setDateText()
	{
		var date = new Date();
		var today = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());

        if (date_id == 'date_today')		// today
        {
        	from_date = today;
        	to_date = today;
        }
        else if (date_id == 'date_yesterday')
        {
        	date.setDate(date.getDate() - 1);

			from_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());
        	to_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());
        }
        else if (date_id == 'date_thisweek')
        {
        	var firstday = date.getDate() + 1;
        	if (date.getDay() == 0)
        		firstday -= 7;
        	else
        		firstday -= date.getDay();

        	date.setDate(firstday);

        	from_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());
        	to_date = today;
        }
        else if (date_id == 'date_thismonth')
        {
        	from_date = getDateText(date.getFullYear(), date.getMonth() + 1, 1);
        	to_date = today;
        }
        else if (date_id == 'date_thisyear')
        {
        	from_date = getDateText(date.getFullYear(), 1, 1);
        	to_date = today;
        }
        else if (date_id == 'date_lastweek')
        {
        	var firstday = date.getDate() + 1 - 7;
        	if (date.getDay() == 0)
        		firstday -= 7;
        	else
        		firstday -= date.getDay();

        	date.setDate(firstday);
        	from_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());

        	firstday = date.getDate() + 6;
        	date.setDate(firstday);
        	to_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());
        }
        else if (date_id == 'date_custom')
        {
        	from_date = '';
        	to_date = '';
        }

        $('#from_date').val(from_date);
        $('#to_date').val(to_date);
	}

	function getDateText(year, month, day)
	{
		var dd = day;
		var mm = month;
		var yyyy = year;

		if (dd < 10)
		    dd = '0' + dd;

		if (mm < 10)
		    mm = '0' + mm;

		return (mm + '/' + dd + '/' + yyyy);
	}

